import axios from "axios";
import moment from "moment";
import logger from "../../services";
import { v4 as uuidv4 } from "uuid";
import { load as cLoad } from "cheerio";
import type { NextApiRequest, NextApiResponse } from "next";

function Pinterest(querry: any) {
  return new Promise(async (resolve, reject) => {
    axios
      .get("https://id.pinterest.com/search/pins/?autologin=true&q=" + querry, {
        headers: {
          cookie:
            '_auth=1; _b="AVna7S1p7l1C5I9u0+nR3YzijpvXOPc6d09SyCzO+DcwpersQH36SmGiYfymBKhZcGg="; _pinterest_; _ir=0',
        },
      })
      .then(({ data }) => {
        const $ = cLoad(data);
        const result: any = [];
        const hasil: any = [];
        $("div > a")
          .get()
          .map((b: any) => {
            const link = $(b).find("img").attr("src");
            result.push(link);
          });
        result.forEach((v: any) => {
          if (v == undefined) return;
          hasil.push(v.replace(/236/g, "736"));
        });
        hasil.shift();
        resolve(hasil);
      });
  });
}

export default async function test(req: NextApiRequest, res: NextApiResponse) {
  try {
    if (req.query.q) {
      const cobra = await Pinterest(req.query.q);
      var _Found = [
        {
          _status: "ðŸŽŠsuccess",
          _uuid: uuidv4(),
          _date_create: moment().format("DD-MM-YYYY hh:mm:ss"),
          _topic: "Pinterest Searcher",
          _query: req.query.q,
          links: cobra,
        },
      ];
      logger.info(_Found);
      return res.send(_Found);
    } else {
      return res.send({
        _status: "Failed with error code 911",
        _date_create: moment().format("DD-MM-YYYY hh:mm:ss"),
        _usage: {
          _api_link: "/api/youtube?q=",
          _example: ["/api/anime?q=death note"],
        },
      });
    }
  } catch (error) {
    return res.status(500).json({
      status: "error",
      message: error.message,
    });
  }
}
